    @Test
    void testCaseIncorrectPath() {
        // Arrange
        String inputFileName = "straight.maz.txt"; // The input file name
        String expectedOutput = "4F"; // Expected output for the correct path
        String incorrectPath = "3F"; // An incorrect path for verification

        // Redirect standard output
        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        PrintStream originalOut = System.out;
        System.setOut(new PrintStream(outputStream));

        // Act
        String[] args = {"-i", inputFileName, "-p", incorrectPath}; // Provide an incorrect path for verification
        Main.main(args);

        // Restore System.out
        System.setOut(originalOut);

        // Assert
        String actualOutput = outputStream.toString().trim();
        assertEquals("incorrect path.", actualOutput); // Check if the output indicates the incorrect path
    }

